public  void    addGroupAndDiscpInComboBox  (lecturer lectObject){
    for(int i   =0;i<lectObject.listGroups.size();i++){
            String res  =    (String)    lectObject.listGroups.get(i);  
            jComboBoxGroup.addItem(res);     
        } 
    for(int i   =0;i<lectObject.listDiscp.size();i++){
            String re  =    (String)    lectObject.listDiscp.get(i);  
            jComboBoxDisc.addItem(re);     
        }
}
public  void    addStudentInComboBox  (String text){
     jComboBox1.addItem(text);
}
